import { statics } from './';
import AdRequest from './AdRequest';
import { SharedEventEmitter } from '../../utils/events';
import { getNativeModule } from '../../utils/native';
import { nativeToJSError } from '../../utils';
let subscriptions = [];
export default class RewardedVideo {
  constructor(admob, adUnit) {
    for (let i = 0, len = subscriptions.length; i < len; i++) {
      subscriptions[i].remove();
    }

    subscriptions = [];
    this._admob = admob;
    this.adUnit = adUnit;
    this.loaded = false;
    SharedEventEmitter.removeAllListeners(`rewarded_video_${adUnit}`);
    SharedEventEmitter.addListener(`rewarded_video_${adUnit}`, this._onRewardedVideoEvent);
  }
  /**
   * Handle a JS emit event
   * @param event
   * @private
   */


  _onRewardedVideoEvent = event => {
    const eventType = `rewarded_video:${this.adUnit}:${event.type}`;
    let emitData = Object.assign({}, event);

    switch (event.type) {
      case 'onAdLoaded':
        this.loaded = true;
        break;

      case 'onAdFailedToLoad':
        emitData = nativeToJSError(event.payload.code, event.payload.message);
        emitData.type = event.type;
        break;

      default:
    }

    SharedEventEmitter.emit(eventType, emitData);
    SharedEventEmitter.emit(`rewarded_video:${this.adUnit}:*`, emitData);
  };
  /**
   * Load an ad with an instance of AdRequest
   * @param request
   * @returns {*}
   */

  loadAd(request) {
    let adRequest = request;

    if (!adRequest || !Object.keys(adRequest)) {
      adRequest = new AdRequest().addTestDevice().build();
    }

    return getNativeModule(this._admob).rewardedVideoLoadAd(this.adUnit, adRequest);
  }
  /**
   * Set custom data
   * @param customData
   * @returns {*}
   */


  setCustomData(customData) {
    return getNativeModule(this._admob).rewardedVideoSetCustomData(this.adUnit, customData);
  }
  /**
   * Return a local instance of isLoaded
   * @returns {boolean}
   */


  isLoaded() {
    return this.loaded;
  }
  /**
   * Show the advert - will only show if loaded
   * @returns {*}
   */


  show() {
    if (this.loaded) {
      getNativeModule(this._admob).rewardedVideoShowAd(this.adUnit);
    }
  }
  /**
   * Listen to an Ad event
   * @param eventType
   * @param listenerCb
   * @returns {null}
   */


  on(eventType, listenerCb) {
    const types = { ...statics.EventTypes,
      ...statics.RewardedVideoEventTypes
    };

    if (!types[eventType]) {
      console.warn(`Invalid event type provided, must be one of: ${Object.keys(types).join(', ')}`);
      return null;
    }

    const sub = SharedEventEmitter.addListener(`rewarded_video:${this.adUnit}:${eventType}`, listenerCb);
    subscriptions.push(sub);
    return sub;
  }

}
